INSERT INTO units (unit_id, unit_type, unit_title, author_id, unit_attributes, unit_keywords, next_unit_id) VALUES ('714bbf2a-c498-11e6-b953-507b9dc82ab8', 'Text', 'Thomas Edison', '5da6bf98-a467-11e6-aedd-4485006d0fd8', '{"markup": "In the late 1880s Thomas Edison built a DC power system for his New York City neighborhood.", "markupType": "None"}', NULL, '8a6641d8-c498-11e6-b953-507b9dc82ab8');
INSERT INTO units (unit_id, unit_type, unit_title, author_id, unit_attributes, unit_keywords, next_unit_id) VALUES ('8a6641d8-c498-11e6-b953-507b9dc82ab8', 'Text', 'George Westinghouse', '5da6bf98-a467-11e6-aedd-4485006d0fd8', '{"markup": "George Westinghouse bought patents for AC power transformers and motors from inventors such as Nikola Tesla.", "markupType": "None"}', NULL, 'a7e3eaee-c498-11e6-b953-507b9dc82ab8');
INSERT INTO units (unit_id, unit_type, unit_title, author_id, unit_attributes, unit_keywords, next_unit_id) VALUES ('b2e9d5ac-c498-11e6-b953-507b9dc82ab8', 'Text', 'Conclusion', '5da6bf98-a467-11e6-aedd-4485006d0fd8', '{"markup": "AC won out as the most viable power distribution method in the â€˜War of Currents''. It is still the way we transmit power today.", "markupType": "None"}', NULL, NULL);
INSERT INTO units (unit_id, unit_type, unit_title, author_id, unit_attributes, unit_keywords, next_unit_id) VALUES ('f74951d8-c497-11e6-b953-507b9dc82ab8', 'Text', 'War of currents', '5da6bf98-a467-11e6-aedd-4485006d0fd8', '{"markup": "In the 1880s industrialists and inventors wanted to find a way to use electricity to power street lights, and to power homes as well. This sparked a war of currents, between AC and DC power.", "markupType": "None"}', NULL, '714bbf2a-c498-11e6-b953-507b9dc82ab8');
INSERT INTO units (unit_id, unit_type, unit_title, author_id, unit_attributes, unit_keywords, next_unit_id) VALUES ('a7e3eaee-c498-11e6-b953-507b9dc82ab8', 'Text', 'Building power systems', '5da6bf98-a467-11e6-aedd-4485006d0fd8', '{"markup": "While Edison built his DC power system, George Westinghouse and his business partners at Westinghouse Electric built his AC power system in Great Barrington, Massachusetts. He then built another in Buffalo, NY.", "markupType": "None"}', NULL, 'b2e9d5ac-c498-11e6-b953-507b9dc82ab8');
